INSERT INTO `audioperef` (`id`, `name`, `brand`, `type`, `model`, `color`, `guarantee`, `country_manufacturer`, `description`, `price`) VALUES
(228, 'DEXP R110', 'DEXP', 'колонки', 'R110', 'белый', 12, 'Китай', 'Колонки DEXP R110 порадуют молодежным стилем. На фоне белого округлого корпуса и черного фона основной лицевой панели контрастно выделяются блестящие металлические круги динамиков, окруженные задорными рыжими вставками. Колонки оборудованы активным усилителем звука, питание на который подается по обычному USB-кабелю. Для подключения к питанию рекомендуется воспользоваться USB-портами версии 2.0 (0.5 А), при подключении к USB-портам версии 3.0 возможны некоторые искажения звука. Допускается подключение к зарядным устройствам мобильных телефонов (при условии выходного тока не больше 0.5 А и отсоединяемого кабеля).', 299),
(229, 'Defender SPK-22', 'Defender', 'колонки', 'SPK-22', 'красный', 3, 'Китай', 'Колонки Defender Defender SPK-22 – компактное устройство, созданное специально для ноутбука. Заявленная мощность колонок равняется 5 Вт. Диапазон воспроизводимых частота – от 200 до 18000 Гц. Эти колонки не нуждаются в питании от сетевого источника. Они функционируют благодаря USB-разъему любого персонального компьютера. В наличии линейный выход (стерео). Звук подается после подсоединения колонок к аудиоразъему компьютера.', 299),
(230, 'Defender SPK-33', 'Defender', 'колонки', 'SPK-33', 'черный', 3, 'Китай', 'Колонки 2.0 Defender SPK-33 – миниатюрная модель, предназначенная для использования с ноутбуком. Питание осуществляется через USB, а аудиосигнал передается через выход 3.5 Jack. Совместимы со смартфонами и планшетами. Обеспечивают звук достаточно хорошего качества.', 299),
(232, 'Aceline AH-108', 'Aceline', 'проводная гарнитура', 'AH-108', 'черный', 12, 'Китай', 'Проводные наушники Aceline AH-108 выполнены в удобной комфортной конструкции с широким оголовьем в классическом черном цвете. Накладные амбушюры оснащены большими динамиками. Звуковая схема представлена в традиционном стереозвучании формата 2.0. Также эти наушники можно использовать в качестве гарнитуры для общения или компьютерных игр по сети. Мембрана излучателей обладает диаметром 30 мм, что позволяет получать глубокий и насыщенный звук. Нижний порог воспроизводимого звукового сигнала составляет 20 Гц, верхний порог частоты, который способна производить гарнитура – 20000 Гц. Уровень звукового давления (чувствительность) равна 105 дБ, значение сопротивления стандартное – 32 Ом.', 250),
(233, 'Defender Aura HN-102', 'Defender', 'проводная гарнитура', 'HN-102', 'черный', 6, 'Китай', 'Накладные наушники Defender Aura HN-102 – это устройство, воспроизводящее гамму звуков от 18 до 20 тыс. Гц, что позволяет передавать басы глубокими и сочными, а высокие ноты звонкими и легкими. Чувствительность этих наушников доходит до уровня 85 дБ, их сопротивление – 32 Ом. Динамики по 30 мм в диаметре равномерно распределяют звук, а амбушюры из мягкого и приятного для кожи материала обеспечивают плотное прилегание без натирания или чувства давления.', 399),
(234, 'Defender Gryphon HN-750', 'Defender', 'проводная гарнитура', 'HN-750', 'синий', 6, 'Китай', 'Наушники Defender Gryphon HN-750 с охватывающими амбушюрами и форматом звуковой системы 2.0 создают качественный звук с диапазоном воспроизводимых частот от 18 до 20 000 герц. Чувствительность модели составляет 105 дБ, а ее сопротивление – 32 Ом. Наушники дополнены встроенным микрофоном с подвижным креплением и чувствительностью -54 дБ. Модель имеет собственный регулятор громкости. Регулируемое оголовье оснащено накладкой из мягкого кожзама и приятными на ощупь амбушюрами. За счет такой конструкции и веса в 220 граммов наушники оставляют только приятные ощущения от использования.', 399),
(235, 'SVEN AP-010MV', 'SVEN', 'проводная гарнитура', 'AP-010MV', 'серебристый', 12, 'Китай', 'Благодаря своей облегченной конструкции и простому дизайну наушники SVEN AP-010MV оптимальны как для прослушивания музыки, так и для общения в сети Интернет. Вертикально расположенная дужка позволяет регулировать оголовье, а мягкие амбушюры обеспечат удобную посадку. Строение накладных стереонаушников обеспечивает плотное прилегание к ушным раковинам, а благодаря равномерно распределенному весу они практически неощутимы. С помощью гибкого поворотного держателя можно подобрать наиболее оптимальное положение микрофона или совсем убрать его, сложив к оголовью.', 350),
(236, 'Aceline AH-115', 'Aceline', 'проводная гарнитура', 'AH-115', 'черный', 12, 'Китай', 'Наушники Aceline AH-115 станут отличным выбором для пользователей, которым требуется по-настоящему легкий и мобильный источник звука, крепящийся с помощью оголовья. Модель, соответствующая формату звуковой схемы 2.0, воспроизводит звук в диапазоне от 20 Гц до 20 кГц. Накладные амбушюры устройства отличаются высоким уровнем удобства эксплуатации.', 399),
(237, 'DEXP OH-01', 'DEXP', 'проводная гарнитура', 'OH-01', 'черный', 12, 'Россия', 'Проводная гарнитура DEXP OH-01 выполнена в черном корпусе и сконструирована для общения. Она содержит одну мягкую чашу амбушюра и крепится на оголовье. Форма чаши позволяет закрыть ушную раковину от посторонних шумов, в результате вы сможете хорошо слышать собеседника: каждое его слово будет четким, и вам не нужно будет ни о чем его переспрашивать.', 399),
(238, 'Ritmix RH-512M', 'Ritmix', 'проводная гарнитура', 'RH-512M', 'черный', 12, 'Китай', 'Наушники RITMIX RH-512 предназначены для работы с компьютером, в частности, для работы с использованием интернет-телефонии. Они весят 70 г. Легкая конструкция позволит сконцентрироваться на текущей задаче, будь это игра или разговор с клиентом. Конденсаторный микрофон обеспечивает хорошую связь и качество звука для разговора.', 299),
(239, 'Turbosky SH-01', 'Turbosky', 'проводная гарнитура', 'SH-01', 'черный', 12, 'Китай', 'Проводные наушники Turbosky SH-01 помогут увлекательно проводить досуг за компьютерными играми и прослушиванием музыки. Они обладают легким пластиковым корпусом и эргономичной конструкцией с мягкими амбушюрами, поэтому не вызывают ощущения дискомфорта даже при длительном пользовании. Благодаря динамическим излучателям с закрытым акустическим оформлением наушники Turbosky SH-01 воспроизводят четкий насыщенный звук. Для подключения к различным устройствам используется стандартный аудиоразъем 3.5 мм с L-образным штекером. Кабель характеризуется длиной 1.2 м.', 299),
(240, 'Dialog Homeline M-200A', 'Dialog', 'проводная гарнитура', 'M-200A', 'черный', 12, 'Китай', 'Проводные наушники Dialog Homeline M-200A – модель со стереоформатом звучания, имеющая самый базовый функционал. Устройство станет хорошим вариантом и для использования дома, и для эксплуатации в офисе. Строгая черная расцветка позволит наушникам хорошо вписаться в периферию, присутствующую на любом компьютерном столе. Акустические характеристики представленной модели позволят ей выдавать качественный звук в диапазоне частот 20-20000 Гц с чувствительностью на уровне 105 дБ. Звучащая в амбушюрах музыка порадует идеальным балансом частот, обеспеченным продуманной конструкцией 30-миллиметровых динамических излучателей.', 310),
(241, 'Gembird MHS-780', 'Gembird', 'проводная гарнитура', 'MHS-780', 'белый', 12, 'Китай', 'Проводная гарнитура Gembird MHS-780 с красно-белым цветовым исполнением подойдет увлеченному геймеру и подарит комфорт во время многочасовых игровых сеансов за счет объемных мягких амбушюр, улавливающих посторонние шумы, и регулируемого оголовья с мягкой накладкой. В ней используется 1.8-метровый кабель с двумя прямыми штекерами типа jack 3.5 мм для раздельного подключения микрофона и наушников к акустическим портам. На кабеле зафиксирован пульт с колесиком, отвечающим за регулировку громкости звука.', 350),
