INSERT INTO `products` (`id`, `name`, `brand`, `type`, `color`, `guarantee`, `country_manufacturer`, `image`, `description`, `price`, `price_old`, `counts`) VALUES
(1, 'Aceline AH-108', 1, 'проводная гарнитура', 'черный', 12, 'Китай', 'img/products/aud1.jpg.webp', 'Проводные наушники Aceline AH-108 выполнены в удобной комфортной конструкции с широким оголовьем в классическом черном цвете. Накладные амбушюры оснащены большими динамиками. Звуковая схема представлена в традиционном стереозвучании формата 2.0. Также эти наушники можно использовать в качестве гарнитуры для общения или компьютерных игр по сети. Мембрана излучателей обладает диаметром 30 мм, что позволяет получать глубокий и насыщенный звук. Нижний порог воспроизводимого звукового сигнала составляет 20 Гц, верхний порог частоты, который способна производить гарнитура – 20000 Гц. Уровень звукового давления (чувствительность) равна 105 дБ, значение сопротивления стандартное – 32 Ом.', 250, 350, 110),
(2, 'Defender Aura HN-102', 2, 'проводная гарнитура', 'черный', 6, 'Китай', 'img/products/aud2.jpg', 'Накладные наушники Defender Aura HN-102 – это устройство, воспроизводящее гамму звуков от 18 до 20 тыс. Гц, что позволяет передавать басы глубокими и сочными, а высокие ноты звонкими и легкими. Чувствительность этих наушников доходит до уровня 85 дБ, их сопротивление – 32 Ом. Динамики по 30 мм в диаметре равномерно распределяют звук, а амбушюры из мягкого и приятного для кожи материала обеспечивают плотное прилегание без натирания или чувства давления.', 399, 499, 110),
(3, 'Defender Gryphon HN-750', 2, 'проводная гарнитура', 'синий', 6, 'Китай', 'img/products/aud3.jpg.webp', 'Наушники Defender Gryphon HN-750 с охватывающими амбушюрами и форматом звуковой системы 2.0 создают качественный звук с диапазоном воспроизводимых частот от 18 до 20 000 герц. Чувствительность модели составляет 105 дБ, а ее сопротивление – 32 Ом. Наушники дополнены встроенным микрофоном с подвижным креплением и чувствительностью -54 дБ. Модель имеет собственный регулятор громкости. Регулируемое оголовье оснащено накладкой из мягкого кожзама и приятными на ощупь амбушюрами. За счет такой конструкции и веса в 220 граммов наушники оставляют только приятные ощущения от использования.', 399, 499, 110),
(4, 'SVEN AP-010MV', 3, 'проводная гарнитура', 'серебристый', 12, 'Китай', 'img/products/aud4.jpg.webp', 'Благодаря своей облегченной конструкции и простому дизайну наушники SVEN AP-010MV оптимальны как для прослушивания музыки, так и для общения в сети Интернет. Вертикально расположенная дужка позволяет регулировать оголовье, а мягкие амбушюры обеспечат удобную посадку. Строение накладных стереонаушников обеспечивает плотное прилегание к ушным раковинам, а благодаря равномерно распределенному весу они практически неощутимы. С помощью гибкого поворотного держателя можно подобрать наиболее оптимальное положение микрофона или совсем убрать его, сложив к оголовью.', 350, 450, 110),
(5, 'Aceline AH-115', 1, 'проводная гарнитура', 'черный', 12, 'Китай', 'img/products/aud5.jpg.webp', 'Наушники Aceline AH-115 станут отличным выбором для пользователей, которым требуется по-настоящему легкий и мобильный источник звука, крепящийся с помощью оголовья. Модель, соответствующая формату звуковой схемы 2.0, воспроизводит звук в диапазоне от 20 Гц до 20 кГц. Накладные амбушюры устройства отличаются высоким уровнем удобства эксплуатации.', 399, 499, 110),
(6, 'DEXP OH-01', 4, 'проводная гарнитура', 'черный', 12, 'Россия', 'img/products/aud6.jpg.webp', 'Проводная гарнитура DEXP OH-01 выполнена в черном корпусе и сконструирована для общения. Она содержит одну мягкую чашу амбушюра и крепится на оголовье. Форма чаши позволяет закрыть ушную раковину от посторонних шумов, в результате вы сможете хорошо слышать собеседника: каждое его слово будет четким, и вам не нужно будет ни о чем его переспрашивать.', 399, 499, 110),
(7, 'Ritmix RH-512M', 5, 'проводная гарнитура', 'черный', 12, 'Китай', 'img/products/aud7.jpg.webp', 'Наушники RITMIX RH-512 предназначены для работы с компьютером, в частности, для работы с использованием интернет-телефонии. Они весят 70 г. Легкая конструкция позволит сконцентрироваться на текущей задаче, будь это игра или разговор с клиентом. Конденсаторный микрофон обеспечивает хорошую связь и качество звука для разговора.', 299, 399, 110),
(8, 'Turbosky SH-01', 6, 'проводная гарнитура', 'черный', 12, 'Китай', 'img/products/aud8.jpg.webp', 'Проводные наушники Turbosky SH-01 помогут увлекательно проводить досуг за компьютерными играми и прослушиванием музыки. Они обладают легким пластиковым корпусом и эргономичной конструкцией с мягкими амбушюрами, поэтому не вызывают ощущения дискомфорта даже при длительном пользовании. Благодаря динамическим излучателям с закрытым акустическим оформлением наушники Turbosky SH-01 воспроизводят четкий насыщенный звук. Для подключения к различным устройствам используется стандартный аудиоразъем 3.5 мм с L-образным штекером. Кабель характеризуется длиной 1.2 м.', 299, 399, 111),
(9, 'Dialog Homeline M-200A', 7, 'проводная гарнитура', 'черный', 12, 'Китай', 'img/products/aud9.jpg.webp', 'Проводные наушники Dialog Homeline M-200A – модель со стереоформатом звучания, имеющая самый базовый функционал. Устройство станет хорошим вариантом и для использования дома, и для эксплуатации в офисе. Строгая черная расцветка позволит наушникам хорошо вписаться в периферию, присутствующую на любом компьютерном столе. Акустические характеристики представленной модели позволят ей выдавать качественный звук в диапазоне частот 20-20000 Гц с чувствительностью на уровне 105 дБ. Звучащая в амбушюрах музыка порадует идеальным балансом частот, обеспеченным продуманной конструкцией 30-миллиметровых динамических излучателей.', 310, 410, 111),
(10, 'Gembird MHS-780', 8, 'проводная гарнитура', 'белый', 12, 'Китай', 'img/products/aud10.jpg.webp', 'Проводная гарнитура Gembird MHS-780 с красно-белым цветовым исполнением подойдет увлеченному геймеру и подарит комфорт во время многочасовых игровых сеансов за счет объемных мягких амбушюр, улавливающих посторонние шумы, и регулируемого оголовья с мягкой накладкой. В ней используется 1.8-метровый кабель с двумя прямыми штекерами типа jack 3.5 мм для раздельного подключения микрофона и наушников к акустическим портам. На кабеле зафиксирован пульт с колесиком, отвечающим за регулировку громкости звука.', 350, 450, 110),
(11, 'Aceline ASP100', 1, 'колонки', 'черный', 12, 'Китай', 'img/products/aud11.jpg.webp', 'Колонки Aceline ASP100 при совместной работе с ноутбуком или стационарным компьютером обеспечат вас глубоким и насыщенным звучанием просматриваемых видеороликов, прослушиваемых музыкальных композиций и любых звуков, возникающих при прохождении компьютерной игры. Модель имеет стильную черно-серую расцветку корпуса и идеально впишется в любое рабочее место. Формат 2.0 подарит вам звучание в стереоформате. Выходной мощности этой акустической системы, достигающей 6 Вт, вполне достаточно для пользователей, которые не любят слишком громкий звук и привыкли использовать акустику в качестве фона во время работы.', 199, 299, 110),
(12, 'ExeGate Disco 140', 9, 'колонки', 'красный', 12, 'Китай', 'img/products/aud12.jpg.webp', 'Колонки 2.0 ExeGate Disco 140 – 2-канальная акустическая система, которая станет надежным дополнением ноутбука или другой техники. Колонки выполнены в компактных пластиковых корпусах с оформлением в красном цвете на фронтальной стороне. Динамики диаметром 50 мм благодаря мощности 6 Вт обеспечивают воспроизведение чистого звука без искажений. Для питания колонок ExeGate Disco 140 применяется интерфейс USB. Передача аудиосигнала осуществляется посредством разъема 3.5 мм.', 199, 299, 110),
(13, 'Defender SPK-22', 2, 'колонки', 'черный', 3, 'Китай', 'img/products/aud13.jpg.webp', 'Колонки Defender SPK-22 – это миниатюрное устройство, разработанное для ноутбука. Их мощность составляет 5 Вт. Минимальная/максимальная воспроизводимая частота – 200 и 18000 Гц соответственно. Колонки не нуждаются в сетевом источнике питания, они работают от USB-разъема компьютера. Выход – линейный (стерео). Для подачи звука их необходимо подключить к аудиоразъему персонального компьютера.', 250, 350, 111),
(14, 'Dialog AC-06UP', 7, 'колонки', 'белый', 12, 'Китай', 'img/products/aud14.jpg.webp', 'Колонки 2.0 Dialog AC-06UP обладают компактными размерами, привлекательным исполнением и высоким качеством звучания. За формирование детализированного звука с насыщенными низкими частотами отвечают стереодинамики диаметром 45 мм суммарной выходной мощностью 6 Вт, которые характеризуются технологией магнитного экранирования. Корпус колонок выполнен из пластика. С помощью регулировочного колесика можно изменять громкость воспроизведения.', 250, 350, 110),
(15, 'Smartbuy MINI', 10, 'колонки', 'белый', 12, 'Китай', 'img/products/aud15.jpg.webp', 'Колонки 2.0 SmartBuy MINI привлекают внимание лаконичным черно-белым дизайном. Компактная акустическая система оснащена пластиковыми корпусами, оформлением в виде закрытого ящика и динамиками по 5 см. Оборудование с общей мощностью 5 Вт поставляется вместе с аудиокабелем.', 250, 350, 110),
(16, 'DEXP R110', 4, 'колонки', 'белый', 12, 'Китай', 'img/products/aud16.jpg.webp', 'Колонки DEXP R110 порадуют молодежным стилем. На фоне белого округлого корпуса и черного фона основной лицевой панели контрастно выделяются блестящие металлические круги динамиков, окруженные задорными рыжими вставками. Колонки оборудованы активным усилителем звука, питание на который подается по обычному USB-кабелю. Для подключения к питанию рекомендуется воспользоваться USB-портами версии 2.0 (0.5 А), при подключении к USB-портам версии 3.0 возможны некоторые искажения звука. Допускается подключение к зарядным устройствам мобильных телефонов (при условии выходного тока не больше 0.5 А и отсоединяемого кабеля).', 299, 399, 110),
(18, 'Defender SPK-33', 2, 'колонки', 'черный', 3, 'Китай', 'img/products/aud17.jpg.webp', 'Колонки 2.0 Defender SPK-33 – миниатюрная модель, предназначенная для использования с ноутбуком. Питание осуществляется через USB, а аудиосигнал передается через выход 3.5 Jack. Совместимы со смартфонами и планшетами. Обеспечивают звук достаточно хорошего качества.', 299, 399, 110),
(19, 'Perfeo PF-128 Wave', 11, 'колонки', 'красный', 12, 'Россия', 'img/products/aud18.png.webp', 'Данные колонки Perfeo PF-128 Wave можно использовать и с другими источниками звука, оснащенными аудиовыходом 3,5мм – игровыми приставками, МР3-плеерами или смартфонами. Компактные колонки легко разместятся на столе, и дополнительная электрическая розетка им не понадобится – питаются они от USB-порта. На кабеле есть регулятор уровня громкости.', 299, 399, 110),
(20, 'Aceline K-202BU', 1, 'клавиатура', 'черная', 12, 'Россия', 'img/products/oth1.jpg.webp', 'Клавиатура проводная Aceline K-202BU с полноразмерной раскладкой станет надежным средством управления в редакторах и других программах. Она выполнена в пластиковом корпусе с надежной конструкцией и отличается практичностью в эксплуатации. Данная клавиатура оборудована 104 клавишами на основе мембранного механизма, которые отличаются четким плавным откликом и низким уровнем шума. Подключение Aceline K-202BU осуществляется посредством проводного интерфейса USB, что гарантирует стабильный сигнал и широкую совместимость.', 299, 0, 0),
(21, 'Aceline K-504BU', 1, 'клавиатура', 'черная', 12, 'Китай', 'img/products/oth2.jpg.webp', 'Клавиатура Aceline K-504BU с мембранным механизмом нажатия является оптимальным выбором для обустройства рабочего места дома или в офисе. Устройство состоит из 104 клавиш, включая цифровой блок. Кнопка Esc выделена ярко-красным цветом, поэтому вы больше не будете путать ее с единицей.', 299, 0, 0),
(22, 'Aceline K-503BU', 1, 'клавиатура', 'черная', 12, 'Китай', 'img/products/oth3.jpg.webp', 'Клавиатура Aceline K-503BU представлена в черном корпусе классической конструкции, что позволит с первого раза ощутить комфорт во время печати, ведь вам не нужно будет привыкать к этому устройству. Модель поддерживает мембранный механизм клавиш, позволяющий легко нажимать на них и обеспечивающий максимально быстрый отклик. К тому же, низкопрофильные клавиши отличаются бесшумным нажатием, поэтому дискомфорта во время работы с клавиатурой не возникает.', 350, 450, 0),
(23, 'Gembird KB-8320U', 8, 'клавиатура', 'черная', 12, 'Китай', 'img/products/oth4.jpg.webp', 'Клавиатура проводная Gembird KB-8320U-Ru_Lat-BL – полноразмерная модель с мембранными клавишами, которая станет отличным приобретением для комфортной работы на домашнем или офисном компьютере. С этим устройством обеспечивается удобство при решении любых задач благодаря цветовому выделению кириллической раскладки, наличию клавиши быстрого переключения языка и цифрового блока для работы с числами. Клавиша Enter имеет крупную форму, которая в отличие от «усеченной» обеспечивает больше комфорта при работе с текстами, что особенно актуально для не слишком опытных пользователей.', 350, 450, 0),
(24, 'Oklick 120M', 12, 'клавиатура', 'черная', 24, 'Китай', 'img/products/oth5.jpg.webp', 'С проводной клавиатурой Oklick 120M вы будете легко и непринужденно бить свои трудовые рекорды, ведь работать с ней – одно удовольствие! Корпус модели выполнен из черного пластика.', 350, 450, 0),
(25, 'Oklick 90MV2', 12, 'клавиатура', 'черная', 24, 'Китай', 'img/products/oth6.jpg.webp', 'Клавиатура проводная Oklick 90MV2 отличается классическим конструктивным исполнением, поэтому вы получаете привычное устройство, которое будет обеспечивать исключительный комфорт в печати. Это клавиатура с мембранным механизмом клавиш, для которой характерно легкое и мягкое нажатие на клавиши. Благодаря этому она подходит для набора большого количества текста.', 350, 450, 0),
(26, 'SVEN KB-S300', 3, 'клавиатура', 'белая', 6, 'Китай', 'img/products/oth7.jpg.webp', 'Клавиатура Sven KB-S300 белого цвета идеально подойдет для подключения к домашнему или рабочему компьютеру. Мембранный механизм клавиатуры делает ее практически бесшумной во время работы. Клавиши нажимаются очень мягко и легко возвращаются в исходную позицию. За счет островной конструкции клавиатуры между клавишами предусмотрено небольшое расстояние, что делает ее удобной для печатания. Высота клавиш средняя, что тоже создает определенный комфорт при работе на клавиатуре. У клавиатуры есть защита от попадания воды внутрь, позволяющая уберечь ее от поломки.', 350, 450, 0),
(27, 'SVEN Standard 301', 3, 'клавиатура', 'черная', 6, 'Китай', 'img/products/oth8.jpg.webp', 'Проводная клавиатура Sven Standard 301 – практичный классический вариант для работы дома и в офисе. Есть клавиша быстрого доступа к калькулятору, что делает модель особенно привлекательной для работников финансовой сферы. Устройство защищено от влаги, что является несомненным преимуществом для любителей выпить чашечку кофе за работой. Подключается к ПК с помощью кабеля USB. Также имеет переходник PS/2. Традиционная раскладка сделает работу легкой и удобной для каждого.', 350, 450, 0),
(28, 'Perfeo MEDIUM', 11, 'клавиатура', 'черная', 12, 'Россия', 'img/products/oth9.png.webp', 'Компактная беспроводная мультимедийная клавиатура PERFEO «MEDIUM». Усовершенствованная 2.4 ГГц технология беспроводной связи. Дистанция работы до 10 метров. Высококачественные мембраны кнопок гарантируют 10 млн нажатий', 399, 499, 0),
(29, 'DEXP K-301WU', 4, 'клавиатура', 'белая', 12, 'Китай', 'img/products/oth10.jpg.webp', 'Клавиатура DEXP K-301WU белого цвета обладает полноразмерной конструкцией и состоит из 104 клавиш с мембранным механизмом нажатия. Кроме стильного дизайна модель может предложить пользователю понятный и простой офисный функционал. Ее корпус защищен от попадания влаги. Это значит, что газировка или кофе не принесут вреда, если прольются на нее. Кнопки модели имеют плавный ход и приятны на ощупь.', 399, 499, 0),
(30, 'Aceline CM-408BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth11.jpg.webp', 'Компактная мышь проводная Aceline CM-408BU выполнена в черном корпусе обтекаемой формы, что делает ее удобной для каждого вне зависимости от того, какой рукой удобнее всего пользоваться. В основе манипулятора лежит светодиодный датчик с поддержкой максимального разрешения 800 dpi: этот показатель остается неизменным, что делает мышь незаменимой для решения повседневных задач. Подключение модели к компьютерным устройствам происходит при помощи USB, поэтому проблем с совместимостью не возникнет. Длина кабеля подключения соответствует 1.2 м.', 99, 199, 0),
(31, 'Aceline CM-704BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth12.jpg.webp', 'Благодаря компактной мыши проводной Aceline CM-704BU вы сможете с комфортом работать за компьютером или ноутбуком не только дома, но и в офисе. Миниатюрные размеры манипулятора позволят всегда держать его при себе, поместив в любой карман сумки или рюкзака для портативного ПК. За счет симметричной формы корпуса устройство адаптировано для управления как правой, так и левой рукой. Необходимую для своей работы электроэнергию модель получает по проводу, при помощи которого она подключается к компьютеру или ноутбуку, не требуя установки соответствующего софта.', 150, 250, 0),
(32, 'Aceline CM-904BU', 1, 'мышь', 'синий', 12, 'Китай', 'img/products/oth13.jpg.webp', 'Мышь проводная Aceline CM-904BU с ярким синим корпусом подойдет для хвата любой рукой. Удобная модель для работы с ребристыми краями приятно лежит в ладони и не выскальзывает. Устройство подключается через USB-разъем с помощью кабеля 1.2 м.', 150, 250, 0),
(33, 'Oklick 385M', 12, 'мышь', 'красный', 12, 'Китай', 'img/products/oth14.jpg.webp', 'Мышь проводная Oklick 385M – универсальная модель. Она адаптирована для использования как со стационарными компьютерами, так и с ноутбуками. Манипулятор обладает компактными габаритами, его масса – 53 г. Можно положить мышку в рюкзак или в сумку, постоянно носить ее с ноутбуком.', 150, 250, 0),
(34, 'Ritmix ROM-111', 5, 'мышь', 'зеленый', 6, 'Китай', 'img/products/oth15.jpg.webp', 'Мышь проводная Ritmix ROM-111 – простая и удобная для работы модель с зеленым корпусом и черными элементами. Используется 3 стандартные кнопки управления, включая скролл (колесо для прокрутки). Чувствительности 800 dpi достаточно для быстрого и точного отклика на ваши команды.', 160, 260, 0),
(35, 'Aceline WM-706BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth16.jpg.webp', 'Компактная мышь беспроводная Aceline WM-706BU выполнена в черном корпусе обтекаемой формы, что делает ее удобной для каждого вне зависимости от того, какой рукой удобнее всего пользоваться. В основе манипулятора лежит светодиодный датчик с поддержкой максимального разрешения 1200 dpi: этот показатель остается неизменным, что делает мышь незаменимой для решения повседневных задач. Подключение модели к компьютерным устройствам происходит при помощи US ресивера, поэтому проблем с совместимостью не возникнет.', 199, 299, 0),
(36, 'Aceline CM-407BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth17.jpg.webp', 'Мышь проводная Aceline CM-407BU черного цвета является оптимальным решением для повседневного использования в офисе. Она обладает лаконичным внешним видом, а ее эргономика предполагает комфортное управление правой рукой. Подключается мышка через разъем USB, а ее провод имеет достаточную длину, чтобы легко ей маневрировать во время работы. Корпус мыши выполнен из матового пластика, что максимально снижает возможность скольжения руки по нему. Поэтому даже при напряженной работе управлять мышкой будет комфортно.', 199, 299, 0),
(37, 'Aceline CM-503BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth18.jpg.webp', 'Мышь проводная Aceline CM-503BU – удобный в эксплуатации манипулятор, который позволит достичь полного контроля над управлением компьютерным устройством. Модель выполнена в стильном сочетании матового и глянцевого пластика, что не только делает ее привлекательным устройством, но и способствует комфорту во время пользования, ведь по матовому покрытию рука не будет скользить.', 199, 299, 0),
(38, 'Aceline CM-710BU', 1, 'мышь', 'черная', 12, 'Китай', 'img/products/oth19.jpg.webp', 'Мышь проводная Aceline CM-710BU – компактная модель в классическом черном цвете корпуса, которая идеально подходит для повседневной работы за компьютером. Устройство обладает стандартной трехкнопочной конструкцией, за счет чего оно удобно для решения любых задач и работы в любых приложениях. В основе функционирования компьютерного манипулятора лежит надежный оптический светодиодный датчик с неизменным разрешением 1000 dpi, обеспечивающий молниеносное перемещение курсора в нужную точку и быстрый отклик на нажатия кнопок.', 199, 299, 0),
(39, 'Aceline CM-710WH', 1, 'мышь', 'белая', 12, 'Китай', 'img/products/oth20.jpg.webp', 'Мышь проводная Aceline CM-710WH – идеальный вариант для повседневной работы за компьютером или ноутбуком. Главным преимуществом этой модели стала симметричная форма корпуса, за счет которой ею будет одинаково удобно пользоваться как правой, так и левой рукой. Оптический светодиодный датчик с неизменным разрешением 1000 dpi обеспечит комфорт при работе в любых приложениях: он хорошо реагирует на любые движения, молниеносно перемещая курсор в нужное место. Оснащение у этой модели стандартное: правая и левая кнопки, а также колесико скролла с возможностью нажатия.', 199, 299, 0),
(40, 'DEXP Leggero M', 4, 'графический планшет', 'черный', 12, 'Россия', 'img/products/oth21.jpg.webp', 'Графический планшет DEXP Leggero M подключается к компьютеру посредством USB. Рабочая область устройства — 240 на 145 миллиметров. Стилус пассивный и имеет чувствительность в 8192 уровня.', 1850, 2850, 0),
(41, 'DEXP Ombra M', 4, 'графический планшет', 'черный', 12, 'Россия', 'img/products/oth22.jpg.webp', 'Графический планшет DEXP Ombra M имеет рабочую область 240 на 155 миллиметров и время отклика 230 точек в секунду. Разрешение экрана 5080 линий на дюйм. В комплекте с планшетом есть пассивное перо, подставка и чехол под него, сменные наконечники, переходник microsUSB и Type-C.', 1950, 2950, 0),
(42, 'HUION Inspiroy H420X', 13, 'графический планшет', 'черный', 24, 'Китай', 'img/products/oth23.jpg.webp', 'Специально выпущенный к десятой годовщине Huion, планшет Inspiroy H420X во многом олицетворяет изначальные замыслы компании. От внешнего вида до характеристик, планшет превосходит ожидания пользователей и дает волю нашему творчеству. Компактный и легкий планшет H420X выполнен в минималистичном стиле. Суженная подставка для ладоней значительно снижает усталость рук при длительном рисовании. Дизайн панели H420X с полным покрытием создает интуитивно понятное пространство для рисования. Кроме того, устройство удобно носить с собой. Комплектное перо H420X, оснащенное передовой технологией EMR без батареи и повышенной скоростью отклика 300 PPS, мгновенно реагирует на каждое движение пера. Курсор будет точно следовать за пером, независимо от того, делаете ли вы быстрый скетч или играете в OSU.', 2399, 3399, 0),
(43, 'XPPen Mini 4', 14, 'графический планшет', 'черная', 12, 'Китай', 'img/products/oth24.jpg.webp', 'Проводной графический планшет XPPen Mini 4 совместим не только с популярными ОС, но даже с Android 6.0 и выше. Прибор подключается через USB-порт Type-C. Комплектация включает в себя беспроводной стилус с 10-ю сменными стержнями и пинцетом для их аккуратной замены, кабелем и адаптером.', 2499, 0, 0),
(44, 'XPPen Star G430S', 14, 'графический планшет', 'черная', 12, 'Китай', 'img/products/oth25.jpg.webp', 'Графический планшет XPPen Star G430S при своих компактных размерах и массе 130 г всегда будет под рукой. Это значит, что пользователь может реализовывать свой потенциал тогда, когда это будет удобно. Рабочая область экрана модели составляет 120х77 мм при разрешении 5080 lpi. При этом беспроводное перо имеет 8192 уровня чувствительности к нажатию, что позволяет рисовать всевозможные скетчи, аниме и варьировать толщину линии.', 2499, 3499, 0),
(45, 'Parblo Ninos N4', 15, 'графический планшет', 'синий', 12, 'Китай', 'img/products/oth26.jpg.webp', 'Графический планшет Parblo Ninos N4 выполнен в компактном корпусе эргономичной формы и обеспечивает высокое удобство ввода информации при взаимодействии с различными графическими приложениями. В данной модели используется рабочая поверхность с размерами 4x3 дюйма. Ввод выполняется при помощи пассивного пера, который поддерживает 8192 уровней чувствительности к нажатию.', 2599, 3599, 0),
(46, 'Defender C-090', 2, 'веб-камера', 'серебристый', 6, 'Китай', 'img/products/oth27.jpg.webp', 'Веб-камера Defender C-090 – камера с хорошей передачей видимости. Сенсорный тип веб-камеры – CMOS. Матрица с количеством точек в 0.3 Мп. Настройка фокуса происходит вручную. В веб-камере предусмотрена поддержка фото- и видеорежимов съемки с разрешением 640x480. Максимальная кадровая частота — 30 кадр/с. Модель дополнена кнопкой мгновенной фотосъемки, позволяющей совершать быстрые фотоснимки.', 699, 799, 0),
(47, 'Canyon CNE-CWC1', 16, 'веб-камера', 'черная', 24, 'Китай', 'img/products/oth28.jpg.webp', 'Оптимальным решением для проведения онлайн-тренингов и небольших конференций станет веб-камера Canyon CNE-CWC1. Помимо передачи видео, модель используется для записи видеороликов и фотосъемки. Качественные снимки с естественной передачей цвета получаются благодаря CMOS-матрице. Частота обновления составляет 30 кадр/с и позволяет вести плавную видеосъемку с разрешением 640х480 пикселей. Встроенный микрофон обладает функцией шумоподавления, что обеспечивает чистую передачу голоса и минимальное количество шумов.', 799, 899, 0),
(48, 'AOC E970SWN', 17, 'Монитор', 'черный', 36, 'Китай', 'img/products/vid1.jpg.webp', 'Монитор AOC E970SWN с диагональю 18.5\" отличается компактностью и функциональностью. Устройство станет отличной составляющей Вашего рабочего компьютера. Модель весьма экономична в потреблении энергии: во время работы монитор поглощает не более 18 Вт, а в режиме ожидания – 0.5 Вт. Устройство соответствует стандарту энергосбережения Energy Star 6.0', 6499, 7499, 0),
(49, 'AOC E2270SWN', 17, 'Монитор', 'черный', 36, 'Китай', 'img/products/vid2.jpg.webp', 'Классический широкоформатный монитор AOC E2270SWN черного цвета с диагональю 21.5\" гарантирует четкость картинки и реалистичную цветопередачу. Широкоформатное устройство оборудовано светодиодной подсветкой типа LED и имеет разрешение экрана 1920x1080. Ваши любимые фильмы теперь возможно смотреть в Full HD, которое поддерживается устройством', 6999, 7999, 0),
(50, 'Acer EK220QAbi', 18, 'Монитор', 'черный', 36, 'Тайвань', 'img/products/vid3.jpg.webp', 'Монитор Acer 21.5\" EK220QAbi позволит вам насладиться качественной картинкой. С VA-дисплеем Full HD 1920 x 1080, широким углом обзора 178 ° и эргономичной подставкой вы сможете с комфортом просматривать изображения и видео с потрясающей детализацией.', 6999, 7999, 0),
(51, 'Acer KA220HQbi', 18, 'Монитор', 'черный', 36, 'Тайвань', 'img/products/vid4.jpg.webp', 'Монитор Acer KA220HQbi с диагональю экрана 21.5 дюймов обеспечит трансляцию изображения в высокочетком разрешении 1920x1080 пикселей. Матовое покрытие экрана гарантирует комфортную работу за монитором, если он установлен рядом с окном или в помещении со слишком ярким освещением. Монитор Acer KA220HQbi располагает двумя видеоинтерфейсами: HDMI и VGA (D-Sub) для подключения к системному блоку. Элегантная подставка обеспечивает устойчивость монитора на компьютерном столе.', 7499, 8499, 0),
(52, 'AOC E2070SWN', 17, 'Монитор', 'черный', 36, 'Китай', 'img/products/vid5.jpg.webp', 'Монитор AOC E2070SWN с диагональю 19.5\" изготовлен в классическом дизайне. Его характеристики и набор опций делают прибор универсальным решением для офиса и домашнего использования. Производительность монитора непременно будет оценена в атмосфере офисной работы.', 7999, 8999, 0),
(53, 'Philips 203V5LSB26', 19, 'Монитор', 'черный', 36, 'Китай', 'img/products/vid6.jpg.webp', 'Монитор Philips 203V5LSB26 с диагональю 19.5\" – это устройство, оснащенное программным обеспечением SmartControl Lite, которое создано для простого управления монитором и настройки всех его параметров. Модель имеет матрицу типа TN+film с реакцией в 5 мс и LED-подсветку из светодиодов. Разрешение монитора – 1600х900, а контрастность (благодаря технологии SmartContrast) – 10M:1. Эти характеристики позволяют устройству передать всю «сочность» и четкость 16.7 млн оттенков. Монитор отлично подходит для использования в офисе и дома.', 7999, 8999, 0),
(54, 'Cablexpert A-VGA-HDMI-01', 20, 'Конвертер', 'черный', 3, 'Китай', 'img/products/vid7.jpg.webp', 'Конвертер Cablexpert VGA - HDMI - Jack 3.5, 0.15 м предназначается для преобразования VGA-сигнала в цифровой стандарт HDMI. Устройство гарантирует высокое качество аудио- и видеосигнала, а также позволяет работать при разрешении до 1920x1200 и частоте 60 Гц. Длина кабеля данного адаптера составляет 15 см, чего достаточно для полноценной эксплуатации.', 1199, 2199, 0),
(55, 'ESPADA EDH14', 21, 'Конвертер', 'белый', 12, 'Китай', 'img/products/vid8.jpg.webp', 'Конвертер ESPADA EDH14 – переходник с HDMI на RCA. С его помощью можно подключать компьютеры и ноутбуки к старым телевизорам, которые лишены распространенного сейчас порта HDMI.', 1999, 2999, 0),
(56, 'ESPADA HSW0301S', 21, 'Переключатель', 'черный', 12, 'Китай', 'img/products/vid9.jpg.webp', 'Переключатель ESPADA HSW0301S пригодится при недостаточном количестве разъемов HDMI в телевизоре или мониторе, или при их неудобном расположении. С его помощью можно подключить сразу три источника сигнала к одному дисплею. Это устройство передает не только видео, но и аудио, гарантировано высокое качество выводимого изображения.', 999, 1999, 0),
(57, 'ORIENT HS0301H', 22, 'Переключатель', 'черный', 12, 'Китай', 'img/products/vid10.jpg.webp', 'Переключатель ORIENT HS0301H позволяет организовать одновременное подключение до трех устройств к телевизору, монитору или проектору посредством разъемов HDMI версии 1.4. Подключение выполняется по одному кабелю, что повышает удобство в эксплуатации. Устройство поддерживает разрешение видео в пределах 3840x2160 пикселей. Переключатель ORIENT HS0301H выполнен в компактном черном корпусе, поэтому не займет много места на полке. С помощью кнопки или пульта ДУ можно легко переключаться между источниками сигнала.', 999, 1999, 0),
(58, 'Cablexpert CC-VGAX2-20CM', 20, 'Разветвитель', 'черный', 12, 'Китай', 'img/products/vid11.jpg.webp', 'Разветвитель Cablexpert CC-VGAX2-20CM представляет из себя простое приспособление, обеспечивающее возможность вывода видеосигнала на два устройства. Например, модель подойдет для совместного использования с двумя мониторами или проекторами. Необходимое условие совместимости – наличие портов VGA (как у источника сигнала, так и у «приемников»). Разветвитель Cablexpert CC-VGAX2-20CM имеет оригинальное исполнение: к корпусу с двумя интегрированными VGA-разъемами присоединен кабель, рассчитанный на подключение к источнику видеосигнала. «Входной» разъем имеет надежные винтовые фиксаторы традиционной конструкции.', 999, 1999, 0),
(59, 'Cablexpert DSP-2PH4-002', 20, 'Разветвитель', 'черный', 12, 'Китай', 'img/products/vid12.jpg.webp', 'Разветвитель Cablexpert DSP-2PH4-002 обеспечивает возможность подсоединения к одному источнику цифрового видеосигнала двух мониторов, телевизоров или других устройств, поддерживающих интерфейс HDMI. Модель отличается простотой использования: для начала работы нужно лишь подключить кабели. Разветвитель характеризуется максимальным разрешением видеосигнала 1920x1080. Такая «картинка» соответствует популярнейшему формату FullHD. Разветвитель Cablexpert DSP-2PH4-002 не займет много места: его длина равна 20 см.', 999, 1999, 0),
(60, 'ORIENT VE041', 22, 'Удлинитель', 'черный', 12, 'Китай', 'img/products/vid13.jpg.webp', 'Удлинитель Orient HDMI-LAN-HDMI представляет собой комплект, позволяющий передавать, а также принимать сигнал HDMI в качестве FHD 1080p/3D на расстоянии до 30 м, что соответствует длине кабеля. Передача сигнала в разрешении Ultra HD 4K осуществляется на расстоянии до 5 м. Устройство поддерживает стандарт HDMI 2.0 и использует для подключения кабель UTP Cat5e/6. Orient HDMI-LAN-HDMI – это удлинитель, который оснащен разъемами HDMI-LAN-HDMI. Изделие не требует питания, а используется в качестве переходника.', 1999, 2999, 0),
(61, 'ORIENT VE042', 22, 'Удлинитель', 'черный', 12, 'Китай', 'img/products/vid14.jpg.webp', 'Удлинитель Orient HDMI-LAN-HDMI представляет собой комплект, позволяющий передавать, а также принимать сигнал HDMI в качестве FHD 1080p/3D на расстоянии до 30 м, что соответствует длине кабеля. Передача сигнала в разрешении Ultra HD 4K осуществляется на расстоянии до 5 м. Устройство поддерживает стандарт HDMI 2.0 и использует для подключения кабель UTP Cat5e/6. Orient HDMI-LAN-HDMI – это удлинитель, который оснащен разъемами HDMI-LAN-HDMI. Изделие не требует питания, а используется в качестве переходника.', 1999, 2999, 0),
(62, 'ORIENT VE020', 22, 'Усилитель сигнала', 'черный', 12, 'Китай', 'img/products/vid15.jpg.webp', 'Приемник сигнала Orient HDMI - HDMI представляет собой простое и функциональное решение, позволяющее увеличить дальность передачи видеосигнала до 40 м. Модель отличается компактными размерами корпуса и оснащена с двух сторон интерфейсами HDMI-F. Максимальное разрешение передачи может достигать 3840x2160 пикселей при частоте обновления до 30 кадр./с.', 699, 799, 0),
(63, 'VCOM DD478', 23, 'Усилитель сигнала', 'черный', 12, 'Китай', 'img/products/vid16.jpg.webp', 'Усилитель HDMI сигнала VCOM DD478 предназначен для стабильной передачи HDMI сигнала c максимальным разрешением 1080р@60HZ на расстояние до 40 метров.', 999, 1999, 0);
