INSERT INTO `videoperef` (`id`, `name`, `brand`, `type`, `model`, `color`, `guarantee`, `country_manufacturer`, `description`, `price`) VALUES
(479, 'Cablexpert A-VGA-HDMI-01', 'Cablexpert', 'Конвертер', 'A-VGA-HDMI-01', 'черный', 3, 'Китай', 'Конвертер Cablexpert VGA - HDMI - Jack 3.5, 0.15 м предназначается для преобразования VGA-сигнала в цифровой стандарт HDMI. Устройство гарантирует высокое качество аудио- и видеосигнала, а также позволяет работать при разрешении до 1920x1200 и частоте 60 Гц. Длина кабеля данного адаптера составляет 15 см, чего достаточно для полноценной эксплуатации.', 1199),
(480, 'ESPADA EDH14', 'ESPADA', 'Конвертер', 'EDH14', 'белый', 12, 'Китай', 'Конвертер ESPADA EDH14 – переходник с HDMI на RCA. С его помощью можно подключать компьютеры и ноутбуки к старым телевизорам, которые лишены распространенного сейчас порта HDMI.', 1999),
(481, 'ESPADA HSW0301S', 'ESPADA', 'Переключатель', 'HSW0301S', 'черный', 12, 'Китай', 'Переключатель ESPADA HSW0301S пригодится при недостаточном количестве разъемов HDMI в телевизоре или мониторе, или при их неудобном расположении. С его помощью можно подключить сразу три источника сигнала к одному дисплею. Это устройство передает не только видео, но и аудио, гарантировано высокое качество выводимого изображения.', 999),
(482, 'ORIENT HS0301H', 'ORIENT', 'Переключатель', 'HS0301H', 'черный', 12, 'Китай', 'Переключатель ORIENT HS0301H позволяет организовать одновременное подключение до трех устройств к телевизору, монитору или проектору посредством разъемов HDMI версии 1.4. Подключение выполняется по одному кабелю, что повышает удобство в эксплуатации. Устройство поддерживает разрешение видео в пределах 3840x2160 пикселей. Переключатель ORIENT HS0301H выполнен в компактном черном корпусе, поэтому не займет много места на полке. С помощью кнопки или пульта ДУ можно легко переключаться между источниками сигнала.', 999),
(483, 'Cablexpert CC-VGAX2-20CM', 'Cablexpert', 'Разветвитель', 'CC-VGAX2-20CM', 'черный', 12, 'Китай', 'Разветвитель Cablexpert CC-VGAX2-20CM представляет из себя простое приспособление, обеспечивающее возможность вывода видеосигнала на два устройства. Например, модель подойдет для совместного использования с двумя мониторами или проекторами. Необходимое условие совместимости – наличие портов VGA (как у источника сигнала, так и у «приемников»). Разветвитель Cablexpert CC-VGAX2-20CM имеет оригинальное исполнение: к корпусу с двумя интегрированными VGA-разъемами присоединен кабель, рассчитанный на подключение к источнику видеосигнала. «Входной» разъем имеет надежные винтовые фиксаторы традиционной конструкции.', 999),
(484, 'Cablexpert DSP-2PH4-002', 'Cablexpert', 'Разветвитель', 'DSP-2PH4-002', 'черный', 12, 'Китай', 'Разветвитель Cablexpert DSP-2PH4-002 обеспечивает возможность подсоединения к одному источнику цифрового видеосигнала двух мониторов, телевизоров или других устройств, поддерживающих интерфейс HDMI. Модель отличается простотой использования: для начала работы нужно лишь подключить кабели. Разветвитель характеризуется максимальным разрешением видеосигнала 1920x1080. Такая «картинка» соответствует популярнейшему формату FullHD. Разветвитель Cablexpert DSP-2PH4-002 не займет много места: его длина равна 20 см.', 999),
(485, 'ORIENT VE041', 'ORIENT', 'Удлинитель', 'VE041', 'черный', 12, 'Китай', 'Удлинитель Orient HDMI-LAN-HDMI представляет собой комплект, позволяющий передавать, а также принимать сигнал HDMI в качестве FHD 1080p/3D на расстоянии до 30 м, что соответствует длине кабеля. Передача сигнала в разрешении Ultra HD 4K осуществляется на расстоянии до 5 м. Устройство поддерживает стандарт HDMI 2.0 и использует для подключения кабель UTP Cat5e/6. Orient HDMI-LAN-HDMI – это удлинитель, который оснащен разъемами HDMI-LAN-HDMI. Изделие не требует питания, а используется в качестве переходника.', 1999),
(486, 'ORIENT VE042', 'ORIENT', 'Удлинитель', 'VE042', 'черный', 12, 'Китай', 'Удлинитель Orient HDMI-LAN-HDMI представляет собой комплект, позволяющий передавать, а также принимать сигнал HDMI в качестве FHD 1080p/3D на расстоянии до 30 м, что соответствует длине кабеля. Передача сигнала в разрешении Ultra HD 4K осуществляется на расстоянии до 5 м. Устройство поддерживает стандарт HDMI 2.0 и использует для подключения кабель UTP Cat5e/6. Orient HDMI-LAN-HDMI – это удлинитель, который оснащен разъемами HDMI-LAN-HDMI. Изделие не требует питания, а используется в качестве переходника.', 1999),
(487, 'ORIENT VE020', 'ORIENT', 'Усилитель сигнала', 'VE020', 'черный', 12, 'Китай', 'Приемник сигнала Orient HDMI - HDMI представляет собой простое и функциональное решение, позволяющее увеличить дальность передачи видеосигнала до 40 м. Модель отличается компактными размерами корпуса и оснащена с двух сторон интерфейсами HDMI-F. Максимальное разрешение передачи может достигать 3840x2160 пикселей при частоте обновления до 30 кадр./с.', 699),
(488, 'VCOM DD478', 'VCOM', 'Усилитель сигнала', 'DD478', 'черный', 12, 'Китай', 'Усилитель HDMI сигнала VCOM DD478 предназначен для стабильной передачи HDMI сигнала c максимальным разрешением 1080р@60HZ на расстояние до 40 метров.', 999),
(489, 'AOC E970SWN', 'AOC', 'Монитор', 'E970SWN', 'черный', 36, 'Китай', 'Монитор AOC E970SWN с диагональю 18.5\" отличается компактностью и функциональностью. Устройство станет отличной составляющей Вашего рабочего компьютера. Модель весьма экономична в потреблении энергии: во время работы монитор поглощает не более 18 Вт, а в режиме ожидания – 0.5 Вт. Устройство соответствует стандарту энергосбережения Energy Star 6.0', 6499),
(490, 'AOC E2270SWN', 'AOC', 'Монитор', 'E2270SWN', 'черный', 36, 'Китай', 'Классический широкоформатный монитор AOC E2270SWN черного цвета с диагональю 21.5\" гарантирует четкость картинки и реалистичную цветопередачу. Широкоформатное устройство оборудовано светодиодной подсветкой типа LED и имеет разрешение экрана 1920x1080. Ваши любимые фильмы теперь возможно смотреть в Full HD, которое поддерживается устройством', 6999),
